mapper.select:
select count(user_id)
from users
where user_id = #{userId}

select *
from users
where user_id = #{Id} and user_pwd = #{Pwd}

mapper.update:
UPDATE users 

	    user_pwd = #{Pwd}
	    nickname = #{Nickname}
	    email_id = #{emailId}
	    email_domain = #{emailDomain}
	    user_image = #{Image}
	    admin = #{Admin}

where user_no = #{userNo}
