mapper.select:
SELECT a.article_no AS article_no, a.title AS title, u.nickname AS author, (SELECT count(*) FROM article_stars s WHERE a.article_no = s.article_no) AS stars, hit_count AS hitCount, a.created_at AS created_at FROM articles a LEFT JOIN users u ON a.user_no = u.user_no WHERE a.deleted_at IS NULL
AND ${key} LIKE CONCAT('%', #{word}, '%')
ORDER BY a.created_at DESC LIMIT ${pgno}, ${spp}

mapper.update:
update articles
set hit_count = hit_count + 1
where article_no = #{articleNo}

update articles set
	title = #{title},
	content = #{content}
where article_no = #{articleNo}

update articles set
	deleted_at = CURRENT_TIMESTAMP
where article_no = #{articleNo}

update articles set
	plan_no = #{planNo}
where article_no = #{articleNo}
