mapper.select:
SELECT plan_no FROM plans
WHERE user_no = #{userNo} 
ORDER BY plan_no DESC 
LIMIT 1;

mapper.update:
UPDATE plans
SET deleted_at = CURRENT_TIMESTAMP
WHERE plan_no = #{planNo}

mapper.select:
SELECT *
FROM plans p
WHERE p.plan_no = #{planNo}
AND p.deleted_at IS NULL;

SELECT *
FROM plans p
JOIN users u ON u.user_no = p.user_no
AND u.user_no = #{userNo}
WHERE p.deleted_at IS NULL;

mapper.update:
UPDATE plans
title = #{title}, description = #{description}, start_at = #{startAt}, end_at = #{endAt},
WHERE plan_no = #{planNo} AND deleted_at IS NULL;

UPDATE plan_destinations
destination_order = #{destinationOrder}, visit_date = #{visitDate}, memo = #{memo}, attraction_no = #{attractionNo},
WHERE destination_no = #{destinationNo}
